mapper.select:
select cp.chat_part_id, cp.chat_room_id, cp.user_id, cp.role, cp.joined_at, u.nickname, u.profile_image
from Chat_Participant cp left join User u on cp.user_id = u.user_id
where cp.chat_room_id = #{roomId}

mapper.update:
update Chat_Participant
set last_read_message_id = #{lastReadMessageId}
where chat_room_id = #{roomId} and user_id = #{userId}
